import React, { useState, useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import axios from "axios";

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  // Check if user is logged in and staff
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get("http://localhost:8000/api/users/me/", {
          withCredentials: true, // session cookies
        });
        setIsLoggedIn(true);
        setIsAdmin(res.data.is_staff);
      } catch (err) {
        setIsLoggedIn(false);
        setIsAdmin(false);
      }
    };
    fetchUser();
  }, [location]);

  const handleLogout = async () => {
    try {
      await axios.post(
        "http://localhost:8000/api/logout/",
        {},
        { withCredentials: true }
      );
    } catch (err) {
      console.warn("Logout failed:", err);
    }
    setIsLoggedIn(false);
    setIsAdmin(false);
    navigate("/login");
  };

  const linkStyle = {
    color: "#fff",
    textDecoration: "none",
    fontSize: "16px",
    padding: "8px 12px",
    borderRadius: "4px",
    transition: "background 0.2s",
  };

  const hoverEffect = { backgroundColor: "rgba(255,255,255,0.2)" };

  const renderLink = (to, label) => (
    <Link
      to={to}
      style={linkStyle}
      onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = hoverEffect.backgroundColor)}
      onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "transparent")}
    >
      {label}
    </Link>
  );

  return (
    <nav
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        padding: "16px 32px",
        backgroundColor: "#f60505",
        color: "#fff",
        position: "sticky",
        top: 0,
        zIndex: 1000,
      }}
    >
      {/* Logo */}
      <div style={{ fontWeight: "bold", fontSize: "20px", color: "#fff", cursor: "default" }}>
        Te Whare RÅ«nanga
      </div>

      {/* Navigation Links */}
      {isLoggedIn && (
        <div style={{ display: "flex", gap: "16px", alignItems: "center" }}>
          {renderLink("/", "Rooms List")}
          {renderLink("/reservation-list", "My Reservations")}
          {renderLink("/make-reservation", "Make Reservation")}
          {isAdmin && renderLink("/admin", "Admin Panel")}
        </div>
      )}

      {/* Auth Buttons */}
      <div style={{ display: "flex", gap: "8px", alignItems: "center" }}>
        {isLoggedIn ? (
          <button
            onClick={handleLogout}
            style={{
              ...linkStyle,
              background: "none",
              border: "1px solid #fff",
              cursor: "pointer",
            }}
            onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = hoverEffect.backgroundColor)}
            onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "transparent")}
          >
            Logout
          </button>
        ) : (
          <>
            {renderLink("/login", "Login")}
            {renderLink("/register", "Register")}
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
