import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { getRooms } from '../services/api';
import './MakeReservation.css';

const MakeReservation = () => {
  const navigate = useNavigate();
  const { roomId } = useParams();

  const [rooms, setRooms] = useState([]);
  const [formData, setFormData] = useState({
    room: roomId || '',
    date: '',
    startTime: '',
    endTime: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    getRooms().then(res => setRooms(res.data));
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    const token = localStorage.getItem('access_token');
    if (!token) {
      setError('Please log in to make a reservation.');
      return;
    }

    if (!formData.room || !formData.date || !formData.startTime || !formData.endTime) {
      setError('Please fill in all fields.');
      return;
    }

    try {
      await axios.post(
        'http://localhost:8000/api/reservations/',
        {
          room: formData.room,
          date: formData.date,
          start_time: formData.startTime,
          end_time: formData.endTime
        },
        {
          headers: {
            'Authorization': `Token ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setSuccess('Reservation created successfully!');
      setTimeout(() => navigate('/'), 1500);
    } catch (err) {
      setError(err.response?.data?.detail || 'Failed to create reservation');
    }
  };

  return (
    <div className="reservation-container">
      <h2>Make a Reservation</h2>

      <div className="reservation-card">
        <div className="label-column">
          <p>Room:</p>
          <p>Date:</p>
          <p>Start Time:</p>
          <p>End Time:</p>
        </div>

        <div className="form-column">
          {error && <p className="error-message">{error}</p>}
          {success && <p className="success-message">{success}</p>}

          <form onSubmit={handleSubmit}>
            <select name="room" value={formData.room} onChange={handleChange} required>
              <option value="">Select a room</option>
              {rooms.map(room => (
                <option key={room._id} value={room._id}>
                  {room.name} (Capacity: {room.capacity})
                </option>
              ))}
            </select>

            <input type="date" name="date" value={formData.date} onChange={handleChange} required />
            <input type="time" name="startTime" value={formData.startTime} onChange={handleChange} required />
            <input type="time" name="endTime" value={formData.endTime} onChange={handleChange} required />

            <div className="button-row">
              <button type="submit" className="reserve-button">Reserve</button>
              <button type="button" className="back-button" onClick={() => navigate('/')}>
                Back to Home
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default MakeReservation;
