import React, { useState, useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import axios from "axios";

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  // Check session login state
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get("http://localhost:8000/api/users/me/", {
          withCredentials: true, // this sends your session cookie
        });
        setIsLoggedIn(true);
        setIsAdmin(res.data.is_staff);
      } catch (err) {
        setIsLoggedIn(false);
        setIsAdmin(false);
      }
    };
    fetchUser();
  }, [location]);

  const handleLogout = async () => {
    try {
      await axios.post(
        "http://localhost:8000/api/logout/",
        {},
        { withCredentials: true }
      );
    } catch (err) {
      console.warn("Logout failed:", err);
    }
    setIsLoggedIn(false);
    setIsAdmin(false);
    navigate("/login");
  };

  const linkStyle = {
    color: "#fff",
    textDecoration: "none",
    fontSize: "16px",
    padding: "8px 12px",
    borderRadius: "4px",
    transition: "background 0.2s",
  };

  const hoverEffect = { backgroundColor: "rgba(255,255,255,0.2)" };

  return (
    <nav
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        padding: "16px 32px",
        backgroundColor: "#f60505",
        color: "#fff",
        position: "sticky",
        top: 0,
        zIndex: 1000,
      }}
    >
      <div style={{ fontWeight: "bold", fontSize: "20px", color: "#fff" }}>
        Te Whare RÅ«nanga
      </div>

      {isLoggedIn && (
        <div style={{ display: "flex", gap: "16px", alignItems: "center" }}>
          <Link to="/" style={linkStyle}>
            Rooms List
          </Link>
          <Link to="/reservation-list" style={linkStyle}>
            My Reservations
          </Link>
          <Link to="/make-reservation" style={linkStyle}>
            Make Reservation
          </Link>
          {isAdmin && (
            <Link to="/admin" style={linkStyle}>
              Admin Panel
            </Link>
          )}
        </div>
      )}

      <div style={{ display: "flex", gap: "8px" }}>
        {isLoggedIn ? (
          <button
            onClick={handleLogout}
            style={{
              ...linkStyle,
              background: "none",
              border: "1px solid #fff",
              cursor: "pointer",
            }}
          >
            Logout
          </button>
        ) : (
          <>
            <Link to="/login" style={linkStyle}>
              Login
            </Link>
            <Link to="/register" style={linkStyle}>
              Register
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
