import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

 const handleSubmit = async (e) => {
  e.preventDefault();
  setError('');
  setIsLoading(true);

  try {
    const response = await axios.post('http://localhost:8000/api-token-auth/', {
      username,
      password,
    });

    const token = response.data.token;
    console.log('Token received:', token);

    localStorage.setItem('access_token', token);
    axios.defaults.headers.common['Authorization'] = `Token ${token}`;


    const userResponse = await axios.get('http://localhost:8000/api/users/me/');
    console.log('User data:', userResponse.data);


    login(
      {
        id: userResponse.data.id,
        username: userResponse.data.username,
        email: userResponse.data.email,
      },
      userResponse.data.is_staff || false
    );


    navigate(userResponse.data.is_staff ? '/admin' : '/');

  } catch (err) {
    console.error('Login error:', err.response?.data || err);
    if (err.response?.status === 400) {
      setError('Invalid credentials. Please try again.');
    } else if (err.response?.status === 404) {
      setError('User endpoint not found. Please check the API configuration.');
    } else {
      setError('Login failed. Please try again later.');
    }
  } finally {
    setIsLoading(false);
  }
};

  return (
    <div style={{ maxWidth: '400px', margin: '50px auto' }}>
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={e => setUsername(e.target.value)}
            required
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <button
          type="submit"
          disabled={isLoading}
          style={{
            width: '60%',
            padding: '12px',
            backgroundColor: '#ef0808',
            color: '#fff',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '16px',
            marginTop: '16px',
            alignSelf: 'center',
            opacity: isLoading ? 0.7 : 1,
          }}
        >
          {isLoading ? 'Logging in...' : 'Login'}
        </button>
      </form>
    </div>
  );
};

export default Login;
