import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Navbar = () => {
  const { user, isAdmin, logout } = useAuth();

  const linkStyle = {
    color: '#fff',
    textDecoration: 'none',
    padding: '8px 12px',
    borderRadius: '4px',
    fontSize: '16px',
  };

  return (
    <nav style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '16px 32px', backgroundColor: '#f60505', color: '#fff' }}>
      <div style={{ fontWeight: 'bold', fontSize: '20px' }}>Te Whare RÅ«nanga</div>

      {user && (
        <div style={{ display: 'flex', gap: '16px', alignItems: 'center' }}>
          <Link to="/" style={linkStyle}>Rooms List</Link>
          <Link to="/reservation-list" style={linkStyle}>My Reservations</Link>
          <Link to="/make-reservation" style={linkStyle}>Make Reservation</Link>
          {isAdmin && <Link to="/admin" style={linkStyle}>Admin Panel</Link>}
        </div>
      )}

      <div>
        {user ? (
          <button onClick={logout} style={{ ...linkStyle, border: '1px solid #fff', background: 'none', cursor: 'pointer' }}>Logout</button>
        ) : (
          <>
            <Link to="/login" style={linkStyle}>Login</Link>
            <Link to="/register" style={linkStyle}>Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
