import React, { useState } from 'react';
import axios from 'axios';
import './AdminAddRoom.css';

const AdminAddRoom = () => {
  const [roomName, setRoomName] = useState('');
  const [capacity, setCapacity] = useState('');
  const [description, setDescription] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleAddRoom = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      await axios.post('/api/rooms', {
        name: roomName,
        capacity,
        description,
      });

      setMessage('Room added successfully!');
      setRoomName('');
      setCapacity('');
      setDescription('');
    } catch (error) {
      console.error(error);
      setMessage('Error adding room.');
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    setRoomName('');
    setCapacity('');
    setDescription('');
    setMessage('');
  };

  return (
    <div className="admin-create-room-container">
      <h3>Add New Room</h3>

      <div className="admin-create-room-card">
        <form onSubmit={handleAddRoom}>
          <div className="form-row">
            <label htmlFor="roomName">Room Name:</label>
            <input
              type="text"
              id="roomName"
              className="form-control"
              value={roomName}
              onChange={(e) => setRoomName(e.target.value)}
              required
            />
          </div>

          <div className="form-row">
            <label htmlFor="capacity">Capacity:</label>
            <input
              type="number"
              id="capacity"
              className="form-control"
              value={capacity}
              onChange={(e) => setCapacity(e.target.value)}
              required
              min="1"
            />
          </div>

          <div className="form-row">
            <label htmlFor="description">Description:</label>
            <textarea
              id="description"
              className="form-control"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              rows="3"
            ></textarea>
          </div>

          <div className="button-row">
            <button type="submit" className="btn-primary" disabled={loading}>
              {loading ? 'Saving...' : 'Save'}
            </button>
            <button type="button" className="btn-secondary" onClick={handleCancel}>
              Cancel
            </button>
          </div>

          {message && (
            <p style={{ textAlign: 'center', marginTop: '15px', color: 'green' }}>
              {message}
            </p>
          )}
        </form>
      </div>
    </div>
  );
};

export default AdminAddRoom;
