import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
  e.preventDefault();
  setError('');
  setIsLoading(true);

  try {
    const response = await axios.post('http://localhost:8000/api-token-auth/', {
      username: username,
      password: password
    });

    const token = response.data.token; // DRF TokenAuthentication returns this

    const userResponse = await axios.get('http://localhost:8000/api/users/me/', {
      headers: {
        'Authorization': `Token ${token}`
      }
    });


    localStorage.setItem('access_token', token);
    axios.defaults.headers.common['Authorization'] = `Token ${token}`;


    login(
      {
        username: userResponse.data.username,
      },
      userResponse.data.is_staff || false
    );

    if (userResponse.data.is_staff) {
      navigate('/admin');
    } else {
      navigate('/');
    }

  } catch (err) {
    console.error('Login error:', err.response?.data || err);
    setError('Login failed. Incorrect username or password');
  } finally {
    setIsLoading(false);
  }
};

  return (
    <div style={{ maxWidth: '400px', margin: '50px auto' }}>
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={e => setUsername(e.target.value)}
            required
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <button
          type="submit"
          disabled={isLoading}
          style={{
            width: '60%',
            padding: '12px',
            backgroundColor: '#ef0808',
            color: '#fff',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '16px',
            marginTop: '16px',
            alignSelf: 'center',
            opacity: isLoading ? 0.7 : 1,
          }}
        >
          {isLoading ? 'Logging in...' : 'Login'}
        </button>
      </form>
    </div>
  );
};

export default Login;
