import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { getRooms } from '../services/api';
import './MakeReservation.css'; // We'll create this CSS file

const MakeReservation = () => {
  const navigate = useNavigate();
  const { roomId } = useParams();
  const [rooms, setRooms] = useState([]);
  const [formData, setFormData] = useState({
    room: roomId || '',
    date: '',
    startTime: '',
    endTime: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    getRooms().then(res => setRooms(res.data));
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    if (!formData.room || !formData.date || !formData.startTime || !formData.endTime) {
      setError('Please fill in all fields');
      return;
    }

    const token = localStorage.getItem('access_token');
    if (!token) {
      setError('You must be logged in to make a reservation');
      return;
    }

    try {
      await axios.post(
        'http://localhost:8000/api/reservations/',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );
      setSuccess('Reservation created successfully!');
      setTimeout(() => navigate('/'), 1500);
    } catch (err) {
      console.error('Reservation error: ', err.response ? err.response.data : err);
      setError(err.response?.data?.detail || 'Failed to make reservation');
    }
  };

  return (
    <div className="reservation-container">
      <h2>Make Reservation</h2>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}
      <form onSubmit={handleSubmit} className="reservation-form">
        <label>Room:</label>
        <select name="room" value={formData.room} onChange={handleChange} required>
          <option value="">Select a room</option>
          {rooms.map(room => (
            <option key={room._id} value={room._id}>
              {room.name} (Capacity: {room.capacity})
            </option>
          ))}
        </select>

        <label>Date:</label>
        <input type="date" name="date" value={formData.date} onChange={handleChange} required />

        <label>Start Time:</label>
        <input type="time" name="startTime" value={formData.startTime} onChange={handleChange} required />

        <label>End Time:</label>
        <input type="time" name="endTime" value={formData.endTime} onChange={handleChange} required />

        <button type="submit" className="reserve-button">Reserve</button>
      </form>
    </div>
  );
};

export default MakeReservation;
