import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Navbar = () => {
  const { user, isAdmin, logout, fetchUser } = useAuth();
  const [isLoggedIn, setIsLoggedIn] = useState(!!user);
  const navigate = useNavigate();
  const location = useLocation();

  // Keep track of login state and fetch user on location change
  useEffect(() => {
    const updateUser = async () => {
      await fetchUser(); // fetch user session from server
      setIsLoggedIn(!!user);
    };
    updateUser();
  }, [location, user, fetchUser]);

  const handleLogout = async () => {
    await logout();
    setIsLoggedIn(false);
    navigate('/login');
  };

  const linkStyle = {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '16px',
    padding: '8px 12px',
    borderRadius: '4px',
    transition: 'background 0.2s',
  };

  const hoverEffect = { backgroundColor: 'rgba(255,255,255,0.2)' };

  const renderLink = (to, label) => (
    <Link
      to={to}
      style={linkStyle}
      onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = hoverEffect.backgroundColor)}
      onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = 'transparent')}
    >
      {label}
    </Link>
  );

  return (
    <nav
      style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '16px 32px',
        backgroundColor: '#f60505',
        color: '#fff',
        position: 'sticky',
        top: 0,
        zIndex: 1000,
      }}
    >
      {/* Logo */}
      <div
        style={{
          fontWeight: 'bold',
          fontSize: '20px',
          color: '#fff',
          cursor: 'default',
        }}
      >
        Te Whare RÅ«nanga
      </div>

      {/* Navigation Tabs */}
      {isLoggedIn && (
        <div style={{ display: 'flex', gap: '16px', alignItems: 'center' }}>
          {renderLink('/', 'Rooms List')}
          {renderLink('/reservation-list', 'My Reservations')}
          {renderLink('/make-reservation', 'Make Reservation')}
          {isAdmin && renderLink('/admin', 'Admin Panel')}
          {isAdmin && renderLink('/admin/create-reservation', 'Create Reservation')}
          {isAdmin && renderLink('/admin/manage-users', 'Manage Users')}
          {isAdmin && renderLink('/admin/manage-reservations', 'Manage Reservations')}
          {isAdmin && renderLink('/admin/manage-rooms', 'Manage Rooms')}
          {isAdmin && renderLink('/admin/create-user', 'Add User')}
          {isAdmin && renderLink('/admin/create-room', 'Add Room')}
        </div>
      )}

      {/* Auth Buttons */}
      <div style={{ display: 'flex', gap: '8px', alignItems: 'center' }}>
        {isLoggedIn ? (
          <button
            onClick={handleLogout}
            style={{ ...linkStyle, background: 'none', border: '1px solid #fff', cursor: 'pointer' }}
            onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = hoverEffect.backgroundColor)}
            onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = 'transparent')}
          >
            Logout
          </button>
        ) : (
          <>
            {renderLink('/login', 'Login')}
            {renderLink('/register', 'Register')}
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
