import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AdminAddUser.css";

const API_URL = "http://localhost:8000/api/";

const AdminAddUser = () => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    is_staff: false,
  });
  const [isAdmin, setIsAdmin] = useState(false);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const checkAdmin = async () => {
      const token = localStorage.getItem("access_token");
      if (!token) return setIsAdmin(false);

      try {
        const res = await axios.get(`${API_URL}users/`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const currentUser = res.data.find((user) => user.is_staff === true);
        setIsAdmin(!!currentUser);
      } catch (error) {
        console.error("Admin check failed:", error);
        setIsAdmin(false);
      }
    };

    checkAdmin();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("access_token");

    try {
      await axios.post(`${API_URL}users/`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessage("✅ User created successfully!");
      setFormData({ username: "", email: "", password: "", is_staff: false });
    } catch (error) {
      console.error("Add user error:", error);
      setMessage("❌ Failed to create user. Make sure you’re logged in as an admin.");
    }
  };

  if (!isAdmin)
    return <p style={{ color: "red" }}>Unauthorized: Please log in as an admin.</p>;

  return (
    <div className="admin-add-user-container">
      <h2>Create New User</h2>
      <form onSubmit={handleSubmit} className="admin-add-user-form">
        <label>
          Username:
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Password:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </label>

        <label className="checkbox-label">
          <input
            type="checkbox"
            name="is_staff"
            checked={formData.is_staff}
            onChange={handleChange}
          />
          Admin privileges
        </label>

        <button type="submit">Create User</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default AdminAddUser;
