import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = "http://localhost:8000/api/";

const AdminAddRoom = () => {
  const [formData, setFormData] = useState({
    name: "",
    capacity: "",
    location: "",
  });
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  // Check if the logged-in user is an admin
  useEffect(() => {
    const checkAdminStatus = async () => {
      const token = localStorage.getItem("access_token");
      if (!token) {
        setIsAdmin(false);
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get(`${API_URL}users/`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Check if any user returned is the current logged-in admin
        // You could also have an endpoint like /users/me/ for cleaner logic
        const adminUser = res.data.find((user) => user.is_staff === true);
        setIsAdmin(!!adminUser);
      } catch (err) {
        console.error("Admin check failed:", err);
        setIsAdmin(false);
      } finally {
        setLoading(false);
      }
    };

    checkAdminStatus();
  }, []);

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("access_token");

    try {
      await axios.post(`${API_URL}rooms/`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setMessage("✅ Room added successfully!");
      setFormData({ name: "", capacity: "", location: "" });
    } catch (error) {
      console.error("Add room error:", error);
      setMessage("❌ Failed to add room. Make sure you are logged in as an admin.");
    }
  };

  if (loading) return <p>Loading...</p>;
  if (!isAdmin) return <p style={{ color: "red" }}>Unauthorized: Please log in as an admin.</p>;

  return (
    <div className="admin-add-room">
      <h2>Add New Room</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Room Name:
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </label>
        <label>
          Capacity:
          <input
            type="number"
            name="capacity"
            value={formData.capacity}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Location:
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleChange}
            required
          />
        </label>
        <button type="submit">Add Room</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default AdminAddRoom;
