import React, { useState, useEffect } from "react";
import ManageRooms from "./ManageRooms";
import ManageReservations from "./ManageReservations";
import ManageUsers from "./ManageUsers";

const AdminPanel = () => {
    const [activeTab, setActiveTab] = useState("rooms");

    return (
        <div style={{ padding: "20px"}}>
            <h1>Admin Panel</h1>
            <div style={{ marginBottom: "20px"}}>
                <button
                    style={{
                        marginRight: "10px",
                        padding: "8px 12px",
                        backgroundColor: activeTab === "rooms" ? "#0738f1" : "#f60505",
                        color: "#fff",
                        border: "none",
                        borderRadius: "4px",
                        cursor: "pointer"
                    }}
                    onClick={() => setActiveTab("rooms")}
                >
                    Manage Rooms
                </button>
                <button
                    style={{
                        marginRight: "10px",
                        padding: "8px 12px",
                        backgroundColor: activeTab === "reservations" ? "#0738f1" : "#f60505",
                        color: "#fff",
                        border: "none",
                        borderRadius: "4px",
                        cursor: "pointer"
                    }}
                    onClick={() => setActiveTab("reservations")}
                >
                    Manage Reservations
                </button>
                <button
                    style={{
                        marginRight: "10px",
                        padding: "8px 12px",
                        backgroundColor: activeTab === "users" ? "#0738f1" : "#f60505",
                        color: "#fff",
                        border: "none",
                        borderRadius: "4px",
                        cursor: "pointer"
                    }}
                    onClick={() => setActiveTab("users")}
                >
                    Manage Users
                </button>
            </div>
            {activeTab === "rooms" && <ManageRooms />}
            {activeTab === "reservations" && <ManageReservations />}
            {activeTab === "users" && <ManageUsers />}
        </div>
    )
}
